package com.legensity.b2bmall.util;

import org.apache.commons.codec.binary.Base32;
import org.apache.commons.codec.binary.Base64;

import javax.crypto.Cipher;
import javax.crypto.spec.SecretKeySpec;

/**
 * AES加密工具类
 **/
public class AESUtil {

    /**
     * AES算法加密明文
     *
     * @param data 明文
     * @param key  密钥，长度16
     * @return 密文
     */
    public static String encryptAES(String data, String key) throws Exception {
        try {
            byte[] raw = key.getBytes("utf-8");
            SecretKeySpec skeySpec = new SecretKeySpec(raw, "AES");
// "算法/模式/补码方式"
            Cipher cipher = Cipher.getInstance("AES/ECB/PKCS5Padding");
            cipher.init(Cipher.ENCRYPT_MODE, skeySpec);
            byte[] encrypted = cipher.doFinal(data.getBytes("utf-8"));
            Base32 base32 = new Base32();
            return new sun.misc.BASE64Encoder().encode(encrypted);
            //return Base64.encodeBase64String(encrypted);
        } catch (Exception e) {
            e.printStackTrace();
            return null;
        }
    }

    /**
     * AES算法解密密文
     *
     * @param data 密文
     * @param key  密钥，长度16
     * @return 明文
     */
    public static String decryptAES(String data, String key) {
        try {
            byte[] raw = key.getBytes("utf-8");
            SecretKeySpec skeySpec = new SecretKeySpec(raw, "AES");
            Cipher cipher = Cipher.getInstance("AES/ECB/PKCS5Padding");
            cipher.init(Cipher.DECRYPT_MODE, skeySpec);
            Base32 base32 = new Base32();
            byte[] encrypted1 = base32.decode(data); // 先用base64解密
            //byte[] encrypted1 = Base64.decodeBase64(data); // 先用base64解密
            try {
                byte[] original = cipher.doFinal(encrypted1);
                String originalString = new String(original, "utf-8");
                return originalString;
            } catch (Exception e) {
                return null;
            }
        } catch (Exception ex) {
            return null;
        }
    }

    public static void main(String[] args) {
        System.out.println(decryptAES("", "hefei.rl.farmer#"));

        try {
            System.out.println(encryptAES("ucPrpXMesHFJY5FQG3ZKZHAHGs34", "hefei.rl.farmer#"));
            System.out.println(encryptAES("hefei.rl.farmer#", "hefei.rl.farmer#"));
            //340111201910300101-SX-001
            String s = "{\n" +
                    "\t\"infoJsonArr\": [{\n" +
                    "\t\t\"ID\": \"PJC1493281035120\",\n" +
                    "\t\t\"GROUP_PERSON_ID\":\"\"\n" +
                    "\t}]\n" +
                    "}";

            System.out.println(encryptAES(s, "hefei.rl.farmer#"));
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}